import React, { Component } from 'react';
import FormAdd from './FormAdd';
import { Button, Modal, Icon } from 'antd';

class ModalAdd extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            row: {},
            visible: false
        };
        this.columnNames = {};
        this.required = {};
        for (const column of this.props.columns) {
            if (column.type !== 'menu') {
                this.state.row[column.id] = '';
                this.columnNames[column.id] = column.name;
                this.required[column.id] = column.required;
            }
        }
        this.form = null;
    }

    showModal = () => {
        this.setState({
            visible: true
        });
    };

    hideModal = () => {
        this.setState({
            visible: false
        });
    };

    addRow = () => {
        if(this.form !== null) {
            this.form.validateFields((err) => {
                if (!err) {
                    this.props.onAdd(Object.assign({}, this.state.row));
                    this.setState({
                        row: this._getEmptyRow(),
                        visible: false
                    });
                    this.form.setFieldsValue(this._getEmptyRow());
                }
            });
        }
    };

    onChange = (columnId, value) => {
        let row = Object.assign({}, this.state.row);
        row[columnId] = value;
        this.setState({
            row
        });
    };

    _getEmptyRow() {
        let newRow = {};
        Object.entries(this.state.row).forEach(([columnId]) => newRow[columnId] = '');
        return newRow;
    }

    setFormRef = (form) => {
        this.form = form;
    };

    render() {
        return (
            <div className="form-add">
                <Button type="primary" className="editable-add-btn" onClick={this.showModal}>
                    <Icon type="plus" />
                    {this.props.buttonText}
                </Button>
                <Modal
                    title="Dodaj nowy"
                    visible={this.state.visible}
                    onOk={this.addRow}
                    onCancel={this.hideModal}
                    okText="Dodaj"
                    cancelText="Anuluj"
                >
                    <FormAdd
                        row={this.state.row} columnNames={this.columnNames} required={this.required}
                        onChange={this.onChange} setFormRef={this.setFormRef}
                    />
                </Modal>
            </div>
        );
    }
}

export default ModalAdd;

import React from 'react';
import EditableCell from './EditableCell';
import Menu from './Menu';

const sorterDecorator = (inputColumn, originalColumn) => {
    inputColumn.sorter = (a, b) => {
        const val1 = a[originalColumn.id].value;
        const val2 = b[originalColumn.id].value;
        if (typeof val1 !== typeof val2) {
            return false;
        }
        switch (typeof val1) {
            case 'number':
                return val1 < val2;
            case 'string':
                return val1.localeCompare(val2);
            default:
                return false;
        }
    };
    return inputColumn;
};

const menuTransformer = (inputColumn, onSaveRow, onEditRowStart, onEditRowCancel, onDeleteRow) => {
    return {
        title: inputColumn.name,
        render: (value, record) => { // eslint-disable-line react/display-name
            return (
                <Menu
                    menuButtons={inputColumn.buttons} loading={record.__loading}
                    editable={record.__editMode} record={record} onSaveRow={onSaveRow}
                    onEditRowStart={onEditRowStart} onEditRowCancel={onEditRowCancel}
                    onDeleteRow={onDeleteRow}
                />
            );
        },
    };
};

const columnTransformer = (
    inputColumns, onHandleChange = () => (null), onSaveCell = () => (null), onEditCellStart = () => (null),
    onEditCellCancel = () => (null), onSaveRow = () => (null), onEditRowStart = () => (null),
    onEditRowCancel = () => (null), onDeleteRow = () => (null)
) => {
    let outputColumns = [], newColumn;
    for (const column of inputColumns) {
        if (column.type === 'menu') {
            newColumn = menuTransformer(column, onSaveRow, onEditRowStart, onEditRowCancel, onDeleteRow);
        } else {
            newColumn = {
                title: column.name,
                dataIndex: column.id,
                render: (value, record) => ( // eslint-disable-line react/display-name
                    <EditableCell prefix={column.prefixRender ? column.prefixRender(value, record, column) : null}
                        editable={column.editable && !record.__editMode}
                        loading={value.__loading || record.__loading} rowId={record.key}
                        colId={value.__name} onHandleChange={onHandleChange} onSave={onSaveCell}
                        onEditStart={onEditCellStart} onEditCancel={onEditCellCancel}
                        value={value.value} editMode={value.__editMode || record.__editMode}
                        __name={value.__name}
                    />
                ),
            };
        }
        if (column.sortable === true) {
            newColumn = sorterDecorator(newColumn, column);
        }
        outputColumns.push(newColumn);
    }
    return outputColumns;
};

export default columnTransformer;

class dataTransformer
{
    static get internalFields() {
        return [
            '__editMode',
            '__loading',
            '__name',
            'key'
        ];
    }
    static data2model = (inputData) => {
        let outputData = [], outputRow, key = 0;
        for (const row of inputData) {
            outputRow = Object.assign({__editMode: false, __loading: false}, row);
            for (const cell in row) {
                outputRow[cell] = {
                    __editMode: false,
                    __loading: false,
                    __name: cell,
                    value: row[cell]
                };
            }
            outputRow.key = key++;
            outputData.push(outputRow);
        }
        return outputData;
    };

    static pureRow = (modelRow) => {
        let row = {};
        Object.entries(modelRow).forEach(([columnName, columnValue]) => {
            if(dataTransformer.internalFields.indexOf(columnName) < 0) {
                row[columnName] = columnValue.value;
            }
        });
        return row;
    }
}

export default dataTransformer;

import React, { Component } from 'react';

import { Table } from 'antd';
import dataTransformer from './dataTransformer';
import columnTransformer from './columnsTransformer';
import ModalAdd from './ModalAdd';

import './index.less';

class EditableTable extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            dataSource: dataTransformer.data2model(this.props.dataSource),
        };
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        if(this.props.dataSource !== nextProps.dataSource) {
            const dataSource = dataTransformer.data2model(nextProps.dataSource);
            this.setState({
                dataSource
            });
        }
    }

    setCellDataProp = (rowId, colId, name, value) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex][colId][name] = value;
        this.setState({
            dataSource
        });
    };

    onHandleChange = (rowId, colId, newValue) => {
        this.setCellDataProp(rowId, colId, 'value', newValue);
    };

    onSaveCell = (rowId, colId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex][colId].__editMode = false;
        dataSource[rowIndex][colId].__loading = true;
        this.setState({
            dataSource
        });
        if(typeof this.props.onChangeCell !== 'undefined') {
            this.props.onChangeCell(rowId, colId, this.state.dataSource[rowIndex][colId].value);
        }
    };

    onEditCancel = (rowId, colId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex][colId].value = dataSource[rowIndex][colId].__oldValue;
        dataSource[rowIndex][colId].__editMode = false;
        this.setState({
            dataSource
        });
    };

    onEditStart = (rowId, colId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex][colId].__oldValue = dataSource[rowIndex][colId].value;
        dataSource[rowIndex][colId].__editMode = true;
        this.setState({
            dataSource
        });
    };

    onSaveRow = (rowId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex].__editMode = false;
        dataSource[rowIndex].__loading = true;
        this.setState({
            dataSource
        });
        if(typeof this.props.onChangeRow !== 'undefined') {
            this.props.onChangeRow(rowId, dataTransformer.pureRow(dataSource[rowIndex]));
        }
    };

    onEditRowStart = (rowId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex].__editMode = true;
        for(const colId in dataSource[rowIndex]) {
            if(dataSource[rowIndex].hasOwnProperty(colId) && colId !== '__editMode'
                && colId !== 'key' && colId !== '__loading'
            ) {
                dataSource[rowIndex][colId].__oldValue = dataSource[rowIndex][colId].value;
            }
        }
        this.setState({
            dataSource
        });
    };

    onEditRowCancel = (rowId) => {
        const rowIndex = this._findRowIndex(rowId);
        let dataSource = this.state.dataSource.slice();
        dataSource[rowIndex].__editMode = false;
        for(const colId in dataSource[rowIndex]) {
            if(dataSource[rowIndex].hasOwnProperty(colId) && colId !== '__editMode'
                && colId !== 'key' && colId !== '__loading'
            ) {
                dataSource[rowIndex][colId].value = dataSource[rowIndex][colId].__oldValue;
            }
        }
        this.setState({
            dataSource
        });
    };

    onAddRow = (row) => {
        this.props.onAddRow(row);
    };

    _findRowIndex(rowId) {
        return this.state.dataSource.findIndex((element) => element.key === rowId);
    }

    render() {
        return (
            <div className={`editable-table ${this.props.className || ''}`}>
                <ModalAdd buttonText="Dodaj monitor" columns={this.props.columns} onAdd={this.onAddRow} />
                <Table
                    size="middle" bordered={true} rowSelection={this.rowSelection} dataSource={this.state.dataSource}
                    columns={columnTransformer(
                        this.props.columns, this.onHandleChange, this.onSaveCell, this.onEditStart, this.onEditCancel,
                        this.onSaveRow, this.onEditRowStart, this.onEditRowCancel, this.props.onDeleteRow
                    )}
                />
            </div>
        );
    }
}

