export class Hostname extends React.Component
{
    constructor(props) {
        super(props);
        this.state = this.getInitialState();
    }

    getInitialState = () => ({
        visible: false,
        searchQuery: '',
        filtered: false,
        dataSource: this.props.dataSource
    });

    inputSearchChange = (searchQuery) => {
        this.setState({
            searchQuery
        });
