    render() {
        const { metadata } = this.props;
        return (
            <div className="metadata-filter-select" onClick={e => e.stopPropagation()}>
                <Select
                    showSearch={true}
                    placeholder="Wybierz metadane"
                    optionFilterProp="children"
                    onChange={this.onChangeType}
                    value={this.state.type}
                    filterOption={(input, option) => (
                        option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                    )}
                >
                    {Object.entries(metadata).map(([id, singleMetadata]) => (
                        <Option key={id}>{singleMetadata.name}</Option>
                    ))}
                </Select>
                <Input
                    onChange={(e) => this.onChangeValue(e.target.value)}
                    onPressEnter={this.onSelect} placeholder="Podaj szukan warto"
                />
                <Button type="primary" onClick={this.onSelect}>Wybierz</Button>
                {
                    this.props.filtered ? <Button type="default" onClick={this.onClear}>Wyczy</Button> : null
                }
            </div>
        );
    }
}

