import React from 'react';
import { Icon, Tooltip } from 'antd';

export class Input extends React.PureComponent {
    render() {
        const {id, value, placeholder, disabled, help, icon, onClear, onChange, children} = this.props;
        let input = null;
        return React.Children.map(children, (child) => (
            React.cloneElement(child, {
                placeholder,
                disabled,
                value,
                prefix: typeof icon !== 'undefined' ? <Icon type={icon} style={{color: 'rgba(0,0,0,.25)'}}/> : null,
                suffix: value && !disabled ? <Icon type="close-circle" onClick={() => {
                    onClear(id);
                    input.focus();
                }}/> : null,
                onChange: !disabled ? (e) => onChange(id, typeof e === 'object' ? e.target.value : e) : () => null,
                ref: node => input = node,
                addonAfter: help ? <Tooltip title={help}><Icon type="question-circle-o"/></Tooltip> : ''
            })
        ));
    }
}

import React from 'react';
import { Select as OriginalSelect } from 'antd';

export class Select extends React.PureComponent {
    render() {
        const {
            id, searchable, disabled, options,
            onChange, value, myValue, ...rest // eslint-disable-line no-unused-vars
        } = this.props;
        return (
            <OriginalSelect
                {...rest}
                value={myValue || undefined}
                showSearch={searchable}
                disabled={disabled}
                onChange={!disabled ? (v) => onChange(id, v) : () => null}
            >
                {
                    options.map(option => {
                        return <OriginalSelect.Option
                            key={option.value} value={option.value}
                        >{option.name}</OriginalSelect.Option>;
                    })
                }
            </OriginalSelect>
        );
    }
}

