class HostList extends React.Component
{
    constructor(props) {
        super(props);
        this.state = this.getInitialState();
    }

    getInitialState = () => ({
        hostToAddMetric: null
    });

    UNSAFE_componentWillReceiveProps(nextProps) {
        if(this.props !== nextProps) {
            this.setState(this.getInitialState());
        }
    }

    showModal = (hostname) => {
        const hostToAddMetric = this.props.hosts.find(host => host.hostname === hostname);
        if(typeof hostToAddMetric !== 'undefined') {
            this.setState({
                hostToAddMetric
            });
        }
    };

    closeModal = () => {
        this.setState({
            hostToAddMetric: null
        });
    };

    allMetadata = (hosts) => {
        const metadata = {};
        hosts.forEach((host) => {
            host.metadata.forEach((singleMetadata) => {
                metadata[singleMetadata.id] = singleMetadata;
            });
        });
        return metadata;
    };

    allMetrics = (hosts) => {
        const base = {}, complex = {};
        hosts.forEach((host) => {
            host.metrics.forEach((metric) => {
                if(metric.parent_id === null) {
                    base[metric.id] = metric.id;
                } else {
                    complex[metric.id] = metric.id;
                }
            });
        });
        return {
            base,
            complex
        };
    };

    render() {
        const columns = [{
            title: 'Host',
            dataIndex: 'displayName',
            key: 'hostname',
            width: '26%',
            render: (hostname, host) => <span><MonitorIcon>{host.monitor}</MonitorIcon> {hostname}</span>
        }, {
            title: 'Metadane',
            dataIndex: 'metadata',
            key: 'metadata',
            width: '37%',
            render: (metadata) => <MetadataCell metadata={metadata}/>,
        }, {
            title: 'Metryki',
            dataIndex: 'metrics',
            key: 'metrics',
            width: '37%',
            render: (metrics, host) => <MetricCell
                onShowModal={this.showModal} onRemove={this.props.onRemoveMetric} host={host}
            />,
        }];

        const hosts = this.props.hosts.map(host => ({
            ...host,
            key: host.hostname,
            displayName: host.hostname
        }));

        return (
            <div className="host-list">
                <NewMetricModal
                    host={this.state.hostToAddMetric} onAddMetric={this.props.onAddMetric} onClose={this.closeModal}
                />
                <HostnameFiltered dataSource={hosts} columns={columns}>
                    <MetricsFiltered metrics={this.allMetrics(this.props.hosts)}>
                        <MetadataFiltered metadata={this.allMetadata(this.props.hosts)}>
                            <Table size="middle" bordered={true} />
                        </MetadataFiltered>
                    </MetricsFiltered>
                </HostnameFiltered>
            </div>
        );
    }
}

export default HostList;

import React from 'react';
import HostList from '../index';
import {fakeData} from './fakeData';

class TableContainer extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
            dataSource: fakeData
        };
    }

    addMetric = (hostname, metric) => {
        const dataSource = this.state.dataSource.slice().map(data => {
            if(data.hostname === hostname) {
                metric.id = Math.floor(Math.random()*1000);
                data.metrics.push(metric);
            }
            return data;
        });
        setTimeout(() => {
            this.setState({
                dataSource
            });
        }, 3000);
    };

    removeMetric = (hostname, metric_id) => {
        const dataSource = this.state.dataSource.slice().map(data => {
            if(data.hostname === hostname) {
                const foundIndex = data.metrics.findIndex(metric => metric.id === metric_id);
                if(foundIndex >= 0) {
                    data.metrics.splice(foundIndex, 1);
                }
            }
            return data;
        });
        setTimeout(() => {
            this.setState({
                dataSource
            });
        }, 3000);
    };

    changeCell = (rowId, colId, value) => {
        let dataSource = this.state.dataSource.slice();
        dataSource[rowId][colId] = value;
        setTimeout(() => {
            this.setState({
                dataSource
            });
        }, 3000);
    };

    changeRow = (rowId, newColumn) => {
        let dataSource = this.state.dataSource.slice();
        dataSource[rowId] = newColumn;
        setTimeout(() => {
            this.setState({
                dataSource
            });
        }, 3000);
    };

    addRow = (row) => {
        let dataSource = this.state.dataSource.slice();
        dataSource.push(row);
        this.setState({
            dataSource
        });
    };

    deleteRow = (rowId) => {
        let dataSource = this.state.dataSource.slice();
        dataSource.splice(rowId, 1);
        this.setState({
            dataSource
        });
    };

    render() {
        return (
            <HostList hosts={fakeData} onAddMetric={this.addMetric} onRemoveMetric={this.removeMetric}/>
        );
    }
}

export default TableContainer;

