const FormItem = Form.Item;

const RegisterForm = ({ form, indeterminate, signUp }) => (
    <Form onSubmit={e => {
        e.preventDefault();
        form.validateFields((err, values) => {
            if (!err) {
                const { password2, ...credentials } = values; // eslint-disable-line no-unused-vars
                signUp(credentials);
            }
        });
    }} className={`login-form ${indeterminate ? 'indeterminate' : ''}`}>
        <FormItem>
            {form.getFieldDecorator('username', {
                rules: [{ required: true, message: 'Nazwa uytkownika musi zosta podana' }],
            })(
                <Input prefix={
                    <Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />
                } placeholder="Nazwa uytkownika" />
            )}
        </FormItem>
        <FormItem>
            {form.getFieldDecorator('password', {
                rules: [{ required: true, message: 'Haso musi zosta podane' }],
            })(
                <Input prefix={
                    <Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />
                } type="password" placeholder="Haso" />
            )}
        </FormItem>
        <FormItem>
            {form.getFieldDecorator('password2', {
                rules: [{ validator: (rule, password2, callback) => {
                    const password = form.getFieldValue('password');
                    if (password2 && password === password2) {
                        callback();
                    }
                    else {
                        callback(true);
                    }
                }, message: 'Hasa muszby identyczne' }],
            })(
                <Input prefix={
                    <Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />
                } type="password" placeholder="Powtrz haso" />
            )}
        </FormItem>
        <FormItem>
            <Button type="primary" htmlType="submit" className="register-form-button">
                Zarejestruj si
            </Button>
        </FormItem>
    </Form>
);

const WrappedRegisterForm = Form.create()(RegisterForm);

export default WrappedRegisterForm;

