const convertMetric = state => {
    const { hosts, ...rest } = state; // eslint-disable-line no-unused-vars
    return rest;
};

const convertHost = state => {
    return {
        metricsInvalidated: false,
        ...state,
        metrics: state.metrics.reduce((c, m) => ({
            ...c,
            [m.id]: convertMetric(m)
        }), {})
    };
};

