import { combineReducers } from 'redux';
import * as types from '../actions/types';

export const getAuthServer = state => state.authServer;
export const getToken = state => state.token;
export const getTokenRequested = state => state.tokenRequested;

const token = (state = null, action) => {
    switch (action.type) {
        case types.SIGN_IN_SUCCESS:
        case types.SIGN_UP_SUCCESS:
            return action.payload.access_token;
        case types.REMOVE_TOKEN:
            return null;
        default:
            return state;
    }
};

const tokenRequested = (state = false, action) => {
    switch (action.type) {
        case types.SIGN_IN_REQUEST:
        case types.SIGN_UP_REQUEST:
            return true;
        case types.SIGN_IN_SUCCESS:
        case types.SIGN_UP_SUCCESS:
        case types.SIGN_IN_FAILURE:
        case types.SIGN_UP_FAILURE:
            return false;
        default:
            return state;
    }
};

const authServer = (state = DEFAULT_AUTH_SERVER, action) => {
    switch (action.type) {
        case types.CHANGE_AUTH_SERVER:
            return action.payload.address || DEFAULT_AUTH_SERVER;
        default:
            return state;
    }
};

export default combineReducers({ token, tokenRequested, authServer });

import { combineReducers } from 'redux';
import * as types from '../actions/types';

export const getHistoricalChartById = (state, id) => state.byId[id];
export const getHistoricalChartsArray = state => state.allIds.map(id => state.byId[id]);

const chart = (state, action) => {
    switch (action.type) {
        case types.ADD_HISTORICAL_CHART:
            return {
                id: action.payload.id,
                metric1: action.payload.metric,
                metric2: null,
                hosts1: [],
                hosts2: null,
                invalidated: false,
                measurements1: {},
                measurements2: null,
                start: action.payload.start,
                end: action.payload.end
            };
        case types.SET_HISTORICAL_CHART_RANGE:
            return {
                ...state,
                ...action.payload
            };
        case types.SET_HISTORICAL_CHART_METRIC1:
            return {
                ...state,
                hosts1: [],
                metric1: action.payload.metric
            };
        case types.SET_HISTORICAL_CHART_METRIC2:
            return {
                ...state,
                metric2: action.payload.metric,
                hosts2: action.payload.metric ? [] : null,
                measurements2: action.payload.metric ? {} : null
            };
        case types.ADD_HISTORICAL_CHART_HOST1:
            return {
                ...state,
                hosts1: [
                    ...state.hosts1,
                    action.payload.host
                ]
            };
        case types.ADD_HISTORICAL_CHART_HOST2:
            return {
                ...state,
                hosts2: [
                    ...state.hosts2,
                    action.payload.host
                ]
            };
        case types.REMOVE_HISTORICAL_CHART_HOST1: {
            const hostIdx = state.hosts1.indexOf(action.meta.host);
            return {
                ...state,
                hosts1: [
                    ...state.hosts1.slice(0, hostIdx),
                    ...state.hosts1.slice(hostIdx + 1)
                ]
            };
        }
        case types.REMOVE_HISTORICAL_CHART_HOST2: {
            const hostIdx = state.hosts2.indexOf(action.meta.host);
            return {
                ...state,
                hosts2: [
                    ...state.hosts2.slice(0, hostIdx),
                    ...state.hosts2.slice(hostIdx + 1)
                ]
            };
        }
        case types.GET_HISTORICAL_MEASUREMENTS_REQUEST:
            return {
                ...state,
                invalidated: true
            };
        case types.GET_HISTORICAL_MEASUREMENTS_SUCCESS:
            return {
                ...state,
                invalidated: false,
                ...action.payload
            };
        default:
            return state;
    }
};

const byId = (state = {}, action) => {
    switch (action.type) {
        case types.ADD_HISTORICAL_CHART:
            return {
                ...state,
                [action.payload.id]: chart(undefined, action)
            };
        case types.REMOVE_HISTORICAL_CHART: {
            const { [action.meta.id]: target, ...rest } = state; // eslint-disable-line no-unused-vars
            return rest;
        }
        case types.SET_HISTORICAL_CHART_RANGE:
        case types.SET_HISTORICAL_CHART_METRIC1:
        case types.SET_HISTORICAL_CHART_METRIC2:
        case types.ADD_HISTORICAL_CHART_HOST1:
        case types.ADD_HISTORICAL_CHART_HOST2:
        case types.REMOVE_HISTORICAL_CHART_HOST1:
        case types.REMOVE_HISTORICAL_CHART_HOST2:
        case types.GET_HISTORICAL_MEASUREMENTS_REQUEST:
        case types.GET_HISTORICAL_MEASUREMENTS_SUCCESS:
            return {
                ...state,
                [action.meta.id]: chart(state[action.meta.id], action)
            };
        default:
            return state;
    }
};

const allIds = (state = [], action) => {
    switch (action.type) {
        case types.ADD_HISTORICAL_CHART:
            return [
                ...state,
                action.payload.id
            ];
        case types.REMOVE_HISTORICAL_CHART: {
            const targetIdx = state.indexOf(action.meta.id);
            return [
                ...state.slice(0, targetIdx),
                ...state.slice(targetIdx + 1)
            ];
        }
        default:
            return state;
    }
};

export default combineReducers({ byId, allIds });

import * as types from '../actions/types';

export const getMonitorByHost = (state, host) => state[host] ? state[host].monitor : null;
export const getHostsArray = state => Object.values(state).map(h => ({ ...h, metrics: Object.values(h.metrics) }));

export const getMetricsAll = state => Array.from(Object.values(state).reduce((set, h) => {
    Object.keys(h.metrics).forEach(m => set.add(m));
    return set;
}, new Set()));

export const getMetrics = state => {
    const built = {};
    for (const host of Object.values(state)) {
        for (const metric of Object.values(host.metrics)) {
            if (!built[metric.id]) {
                built[metric.id] = {};
            }
            built[metric.id][host.hostname] = metric;
        }
    }
    return built;
};

const extractHosts = (state, monitor) => Object.entries(state)
    .reduce(
        (c, [ key, obj ]) => {
            const type = obj.monitor === monitor ? 'found' : 'rest';
            return {
                ...c,
                [type]: {
                    ...c[type],
                    [key]: obj
                }
            };
        },
        {
            found: {},
            rest: {}
        }
    );

const setMonitor = (state, monitor) => Object.entries(state)
    .map(([ key, obj ]) => [ key, { ...obj, monitor } ])
    .reduce((c, [ key, obj ]) => ({ ...c, [key]: obj }), {});

const convertMetric = state => {
    const { hosts, ...rest } = state; // eslint-disable-line no-unused-vars
    return rest;
};

const normalize = state => state.reduce((c, obj) => ({
    ...c,
    [obj.hostname]: {
        ...obj,
        metrics: obj.metrics.reduce((d, metric) => ({
            ...d,
            [metric.id]: convertMetric(metric)
        }), {}),
        metricsInvalidated: false
    }
}), {});

const host = (state, action) => {
    switch (action.type) {
        case types.ADD_COMPLEX_METRIC_REQUEST:
        case types.REMOVE_COMPLEX_METRIC_REQUEST:
            return {
                ...state,
                metricsInvalidated: true
            };
        case types.ADD_COMPLEX_METRIC_SUCCESS:
            return {
                ...state,
                metricsInvalidated: false,
                metrics: {
                    ...state.metrics,
                    [action.payload.id]: {
                        ...action.payload,
                        removable: true
                    }
                }
            };
        case types.ADD_COMPLEX_METRIC_FAILURE:
        case types.REMOVE_COMPLEX_METRIC_FAILURE:
            return {
                ...state,
                metricsInvalidated: false
            };
        case types.REMOVE_COMPLEX_METRIC_SUCCESS: {
            const {
                [action.meta.id]: metricToRemove, // eslint-disable-line no-unused-vars
                ...metricsToLeave
            } = state.metrics;
            return {
                ...state,
                metricsInvalidated: false,
                metrics: metricsToLeave
            };
        }
        default:
            return state;
    }
};

export default (state = {}, action) => {
    switch (action.type) {
        case types.GET_HOSTS_SUCCESS:
            return {
                ...extractHosts(state, action.meta.monitor).rest,
                ...setMonitor(normalize(action.payload), action.meta.monitor)
            };
        case types.SET_MONITOR_ADDRESS: {
            const hostsExtracted = extractHosts(state, action.meta.monitor);
            return {
                ...hostsExtracted.rest,
                ...setMonitor(hostsExtracted.found, action.payload.address)
            };
        }
        case types.REMOVE_MONITOR:
            return extractHosts(state, action.meta.monitor).rest;
        case types.REMOVE_TOKEN:
            return {};
        case types.ADD_COMPLEX_METRIC_REQUEST:
        case types.ADD_COMPLEX_METRIC_SUCCESS:
        case types.ADD_COMPLEX_METRIC_FAILURE:
        case types.REMOVE_COMPLEX_METRIC_REQUEST:
        case types.REMOVE_COMPLEX_METRIC_SUCCESS:
        case types.REMOVE_COMPLEX_METRIC_FAILURE: {
            const { [action.meta.host]: target, ...rest } = state;
            return {
                ...rest,
                [action.meta.host]: host(target, action)
            };
        }
        default:
            return state;
    }
};

