const monitor = (state = {}, action) => {
    switch (action.type) {
        case types.ADD_MONITOR:
            return {
                ...action.payload,
                hostsRequested: false
            };
        case types.SET_MONITOR_ADDRESS:
            return {
                ...state,
                address: action.payload.address
            };
        case types.SET_MONITOR_DESCRIPTION:
            return {
                ...state,
                description: action.payload.description
            };
        case types.GET_HOSTS_REQUEST:
            return {
                ...state,
                hostsRequested: true
            };
        case types.GET_HOSTS_SUCCESS:
        case types.GET_HOSTS_FAILURE:
        case types.REMOVE_TOKEN:
            return {
                ...state,
                hostsRequested: false
            };
        default:
            return state;
    }
};

export default (state = defaultMonitorsObj, action) => {
    switch (action.type) {
        case types.ADD_MONITOR:
            return {
                ...state,
                [action.payload.address]: monitor(undefined, action)
            };
        case types.SET_MONITOR_ADDRESS: {
            const { [action.meta.monitor]: target, ...rest } = state;
            return {
                ...rest,
                [action.payload.address]: monitor(target, action)
            };
        }
        case types.SET_MONITOR_DESCRIPTION:
        case types.GET_HOSTS_REQUEST:
        case types.GET_HOSTS_SUCCESS:
        case types.GET_HOSTS_FAILURE:
            return {
                ...state,
                [action.meta.monitor]: monitor(state[action.meta.monitor], action)
            };
        case types.REMOVE_TOKEN:
            return Object.entries(state).reduce((c, [ k, v ]) => ({
                ...c,
                [k]: monitor(v, action)
            }), {});
        case types.REMOVE_MONITOR: {
            const { [action.meta.monitor]: toRemove, ...notRemoved } = state; // eslint-disable-line no-unused-vars
            return notRemoved;
        }
        default:
            return state;
    }
};

import React from 'react';
import { connect } from 'react-redux';
import { Switch, Route } from 'react-router';
import Historical from './pages/Historical';
import Live from './pages/Live';
import Metrics from './pages/Metrics';
import Monitors from './pages/Monitors';
import NotFound from './pages/NotFound';
import SignIn from './pages/SignIn';
import SignUp from './pages/SignUp';
import { getToken } from './reducers';

export const LIVE_ROUTE = '/live';
export const HISTORICAL_ROUTE = '/historical';
export const MONITORS_ROUTE = '/monitors';
export const HOSTS_ROUTE = '/hosts';
export const LOGIN_ROUTE = '/login';
export const REGISTRATION_ROUTE = '/registration';

export const routes = {
    [LIVE_ROUTE]: {
        tokenRequired: true,
        component: Live
    },
    [HISTORICAL_ROUTE]: {
        tokenRequired: true,
        component: Historical
    },
    [MONITORS_ROUTE]: {
        tokenRequired: true,
        component: Monitors
    },
    [HOSTS_ROUTE]: {
        tokenRequired: true,
        component: Metrics
    },
    [LOGIN_ROUTE]: {
        tokenRequired: false,
        component: SignIn
    },
    [REGISTRATION_ROUTE]: {
        tokenRequired: false,
        component: SignUp
    }
};

export const isRouteDefined = route => Boolean(routes[route]);

export const getDefaultRouteByToken = token => token ? LIVE_ROUTE : LOGIN_ROUTE;

export const isTokenRequired = route => routes[route].tokenRequired;

const mapStateToProps = state => ({
    token: getToken(state)
});

const mapDispatchToProps = {};

const wrapWithAuth = tokenRequired => Component => connect(mapStateToProps, mapDispatchToProps)(

    function WithAuth({ token, ...rest }) {
        if (Boolean(tokenRequired) === Boolean(token)) {
            return React.createElement(Component, rest);
        }
        else {
            return null;
        }
    }

);

export const getRoutes = () => (
    <Switch>
        {Object.entries(routes).map(([ path, r ]) => (
            <Route key={path} path={path} component={wrapWithAuth(r.tokenRequired)(r.component)} />
        ))}
        <Route component={NotFound} />
    </Switch>
);

import moment from 'moment';
import escapeStringRegexp from 'escape-string-regexp';
import { select, call, put, all } from 'redux-saga/effects';
import { getAuthServer, getToken, getMonitorByHost, getLiveChartByMonitors, getLiveChartLastTime,
    getHistoricalChartsByMonitors, getLiveChartById, getHistoricalChartById } from '../reducers';
