export function* getLiveChartMeasurementsForSingleMonitor(initialFetch, id, monitor, hosts) {
    const token = yield select(getToken);
    const { metric } = yield select(getLiveChartById, id);
    let start;
    if (!initialFetch) {
        start = yield select(getLiveChartLastTime, id, monitor);
    }
    if (!start) {
        start = yield call(getStartTimeForLiveCharts);
    }
    try {
        const q = `metric_id:${metric},hostname:/(${hosts.map(escapeStringRegexp).join('|')})/`;
        const data = yield call(api.getMeasurements, monitor, token, { start, q });
        return { data };
    }
    catch (error) {
        return error;
    }
}

export function* getLiveChartMeasurements({ meta }) {
    let { measurements, hostsByMonitors } = yield select(getLiveChartByMonitors, meta.id);
    if (meta.initialFetch) {
        measurements = {};
    }
    if (Object.keys(hostsByMonitors).length) {
        const results = yield all(
            Object.entries(hostsByMonitors).reduce((c, [ monitor, hosts ]) => ({
                ...c,
                [monitor]: call(
                    getLiveChartMeasurementsForSingleMonitor,
                    meta.initialFetch,
                    meta.id,
                    monitor,
                    hosts
                )
            }), {})
        );
        for (const monitor of Object.keys(results)) {
            const result = results[monitor];
            if (!result.data) {
                yield put(actions.getLiveMeasurementsFailure(result, meta.id, monitor));
            }
            else {
                for (const measurement of result.data) {
                    if (meta.initialFetch) {
                        measurements[measurement.hostname] = measurement.data;
                    }
                    else {
                        const hostMeas = measurements[measurement.hostname];
                        const start = yield call(getStartTimeForLiveCharts);
                        const startMom = moment(start);
                        let i = 0;
                        while (i < hostMeas.length && startMom.isAfter(hostMeas[i].time)) {
                            ++i;
                        }
                        measurements[measurement.hostname] = [
                            ...hostMeas.slice(i),
                            ...measurement.data
                        ];
                    }
                }
            }
        }
    }
    yield put(actions.getLiveMeasurementsSuccess({ measurements }, meta.id));
}

export function* getHistoricalChartMeasurementsForSingleMonitor(id, monitor, hosts1, hosts2) {
    const token = yield select(getToken);
    const { metric1, metric2, start, end } = yield select(getHistoricalChartById, id);
    try {
        let q = [];
        if (hosts1) {
            q.push(`metric_id:${metric1},hostname:/(${hosts1.map(escapeStringRegexp).join('|')})/`);
        }
        if (hosts2) {
            q.push(`metric_id:${metric2},hostname:/(${hosts2.map(escapeStringRegexp).join('|')})/`);
        }
        q = q.join(';');
        const data = yield call(api.getMeasurements, monitor, token, { start, end, q });
        return { data };
    }
    catch (error) {
        return error;
    }
}

export function* getHistoricalChartMeasurements({ meta }) {
    const { metric2 } = yield select(getHistoricalChartById, meta.id);
    let measurements1 = {};
    let measurements2 = metric2 ? {} : null;
    const hostsByMetricsAndMonitors = yield select(getHistoricalChartsByMonitors, meta.id);
    if (Object.keys(hostsByMetricsAndMonitors).length) {
        const results = yield all(
            Object.entries(hostsByMetricsAndMonitors).reduce((c, [ monitor, { hosts1, hosts2 }]) => ({
                ...c,
                [monitor]: call(
                    getHistoricalChartMeasurementsForSingleMonitor,
                    meta.id,
                    monitor,
                    hosts1,
                    hosts2
                )
            }), {})
        );
        for (const monitor of Object.keys(results)) {
            const result = results[monitor];
            if (!result.data) {
                yield put(actions.getHistoricalMeasurementsFailure(result, meta.id, monitor));
            }
            else {
                for (const measurement of result.data) {
                    if (measurement.metric_id === metric2) {
                        measurements2[measurement.hostname] = measurement.data;
                    }
                    else {
                        measurements1[measurement.hostname] = measurement.data;
                    }
                }
            }
        }
    }
    yield put(actions.getHistoricalMeasurementsSuccess({ measurements1, measurements2 }, meta.id));
}

import { all, fork, select, put, cancel, takeEvery, takeLatest, take, call } from 'redux-saga/effects';
import { delay } from 'redux-saga';
import { push } from 'connected-react-router';
import * as types from '../actions/types';
import * as actions from '../actions/sync';
import * as apiSagas from './api';
import * as storageSagas from './localStorage';
import { getToken, getPathname, getLiveChartsAllIds, getMonitorsAddresses } from '../reducers';
import { getDefaultRouteByToken, isRouteDefined, isTokenRequired, LIVE_ROUTE } from '../routes';
import Notification from '../components/Notification';

function* dealWithNewLocation(pathname) {
    const isTokenSet = Boolean(yield select(getToken));
    if (pathname === '/') {
        const newPathname = yield call(getDefaultRouteByToken, isTokenSet);
        yield put(push(newPathname));
    }
    else if (yield call(isRouteDefined, pathname)) {
        const tokenRequired = yield call(isTokenRequired, pathname);
        if (tokenRequired === isTokenSet) {
            yield put(actions.changeRoutingContext({ pathname }));
        }
        else {
            const newPathname = yield call(getDefaultRouteByToken, isTokenSet);
            yield put(push(newPathname));
        }
    }
    else {
        yield put(actions.changeRoutingContext({ pathname }));
    }
}

function* watchForLocationChange() {
    yield delay(0);
    const startLocation = yield select(getPathname);
    let task = yield fork(dealWithNewLocation, startLocation);
    while (true) {
        const action = yield take([
            '@@router/LOCATION_CHANGE',
            types.SIGN_IN_SUCCESS,
            types.SIGN_UP_SUCCESS,
            types.REMOVE_TOKEN
        ]);
        yield cancel(task);
        const newLocation = action.type === '@@router/LOCATION_CHANGE' ? action.payload.location.pathname : '/';
        task = yield fork(dealWithNewLocation, newLocation);
    }
}

function* keepLiveChartRefreshing(id) {
    yield put(actions.getLiveMeasurementsRequest(id, true));
    while(true) {
        yield all([
            take(types.GET_LIVE_MEASUREMENTS_SUCCESS),
            delay(1000 * 15)
        ]);
        yield put(actions.getLiveMeasurementsRequest(id, false));
    }
}

function* conductLiveChartsUpdates() {
    const liveCharts = yield select(getLiveChartsAllIds);
    const tasks = {};
    for (const id of liveCharts) {
        tasks[id] = yield fork(keepLiveChartRefreshing, id);
    }
    while (true) {
        const action = yield take([
            types.CHANGE_ROUTING_CONTEXT,
            types.ADD_LIVE_CHART,
            types.REMOVE_LIVE_CHART,
            types.SET_LIVE_CHART_METRIC,
            types.ADD_LIVE_CHART_HOST,
            types.REMOVE_LIVE_CHART_HOST
        ]);
        if (action.type === types.CHANGE_ROUTING_CONTEXT && action.payload.pathname !== LIVE_ROUTE) {
            yield cancel();
        }
        if (
            action.type === types.REMOVE_LIVE_CHART
            || action.type === types.SET_LIVE_CHART_METRIC
            || action.type === types.ADD_LIVE_CHART_HOST
            || action.type === types.REMOVE_LIVE_CHART_HOST
        ) {
            yield cancel(tasks[action.meta.id]);
        }
        if (action.type === types.ADD_LIVE_CHART) {
            tasks[action.payload.id] = yield fork(keepLiveChartRefreshing, action.payload.id);
        }
        if (
            action.type === types.SET_LIVE_CHART_METRIC
            || action.type === types.ADD_LIVE_CHART_HOST
            || action.type === types.REMOVE_LIVE_CHART_HOST
        ) {
            tasks[action.meta.id] = yield fork(keepLiveChartRefreshing, action.meta.id);
        }
    }
}

function* watchForLiveChartContext() {
    let task;
    const pathname = yield select(getPathname);
    if (pathname === LIVE_ROUTE) {
        task = yield fork(conductLiveChartsUpdates);
    }
    while (true) {
        const { payload } = yield take(types.CHANGE_ROUTING_CONTEXT);
        if (payload.pathname === LIVE_ROUTE) {
            if (task) {
                cancel(task);
            }
            task = yield fork(conductLiveChartsUpdates);
        }
    }
}

function* loginWatcher() {
    yield take(types.CHANGE_ROUTING_CONTEXT);
    const token = yield select(getToken);
    let worker;
    if (token) {
        worker = yield fork(authSiteWatcher);
        yield call(Notification.success, 'Sesja zostaa przywrcona');
    }
    while (true) {
        const { type } = yield take([
            types.SIGN_IN_SUCCESS,
            types.SIGN_UP_SUCCESS,
            types.REMOVE_TOKEN
        ]);
        if (worker) {
            yield cancel(worker);
        }
        if (type !== types.REMOVE_TOKEN) {
            worker = yield fork(authSiteWatcher);
            yield call(Notification.success, 'Zostae zalogowany pomylnie');
        }
        else {
            yield call(Notification.success, 'Zostae wylogowany pomylnie');
        }
    }
}

function* getAllHosts() {
    const monitors = yield select(getMonitorsAddresses);
    yield all(monitors.map(monitor => put(actions.getHostsRequest(monitor))));
}

function* watchForHistoricalChartUpdates() {
    let workers = {};
    while (true) {
        const action = yield take([
            types.SET_HISTORICAL_CHART_RANGE,
            types.SET_HISTORICAL_CHART_METRIC1,
            types.SET_HISTORICAL_CHART_METRIC2,
            types.ADD_HISTORICAL_CHART_HOST1,
            types.ADD_HISTORICAL_CHART_HOST2,
            types.REMOVE_HISTORICAL_CHART_HOST1,
            types.REMOVE_HISTORICAL_CHART_HOST2,
            types.REMOVE_HISTORICAL_CHART
        ]);
        if (workers[action.meta.id]) {
            yield cancel(action.meta.id);
        }
        if (action.type !== types.REMOVE_HISTORICAL_CHART) {
            yield put(actions.getHistoricalMeasurementsRequest(action.meta.id));
        }
    }
}

function* authSiteWatcher() {
    yield all([
        fork(watchForLiveChartContext),
        fork(watchForHistoricalChartUpdates),
        takeEvery(types.GET_HOSTS, getAllHosts),
        takeEvery(types.ADD_COMPLEX_METRIC_REQUEST, apiSagas.addComplexMetric),
        takeEvery(types.REMOVE_COMPLEX_METRIC_REQUEST, apiSagas.removeComplexMetric),
        takeEvery(types.GET_HOSTS_REQUEST, apiSagas.getHosts),
        takeEvery(types.GET_LIVE_MEASUREMENTS_REQUEST, apiSagas.getLiveChartMeasurements),
        takeEvery(types.GET_HISTORICAL_MEASUREMENTS_REQUEST, apiSagas.getHistoricalChartMeasurements),
        takeLatest([
            types.ADD_MONITOR,
            types.SET_MONITOR_ADDRESS,
            types.SET_MONITOR_DESCRIPTION,
            types.REMOVE_MONITOR
        ], storageSagas.monitorsSaver),
    ]);
    yield put(actions.getHosts());
}

function* errorThrower() {
    while (true) {
        const action = yield take('*');
        if (action.error) {
            yield call(Notification.error, action.payload.message + (status ? ' (Kod ' + status + ')' : ''));
        }
    }
}

export default function* root() {
    yield all([
        takeEvery(types.SIGN_UP_REQUEST, apiSagas.signUp),
        takeEvery(types.SIGN_IN_REQUEST, apiSagas.signIn),
        takeLatest(types.SIGN_IN_REQUEST, storageSagas.tokenSaver),
        takeLatest(types.REMOVE_TOKEN, storageSagas.tokenEraser),
        takeLatest(types.CHANGE_AUTH_SERVER, storageSagas.authServerSaver),
        fork(watchForLocationChange),
        fork(loginWatcher),
        fork(errorThrower)
    ]);
}

import { take, call, select } from 'redux-saga/effects';
import * as types from '../actions/types';
import { convertMonitorsFromObj } from '../reducers';
import * as storage from '../localStorage';

export function* tokenSaver({ payload }) {
    if (payload.remember) {
        const action = yield take(types.SIGN_IN_SUCCESS);
        yield call(storage.saveToken, action.payload.access_token);
    }
}

export function* tokenEraser() {
    yield call(storage.clearToken);
}

export function* monitorsSaver() {
    const monitors = yield select(convertMonitorsFromObj);
    yield call(storage.updateMonitors, monitors);
}

export function* authServerSaver({ payload }) {
    yield call(storage.updateAuthServer, payload.address);
}

import { configure } from 'enzyme';
import Adapter from 'enzyme-adapter-react-16';

configure({ adapter: new Adapter() });

class LocalStorageMock {

    constructor() {
        this.store = {};
    }

    clear() {
        this.store = {};
    }

    getItem(key) {
        return this.store[key] || null;
    }

    setItem(key, value) {
        this.store[key] = value;
    }

    removeItem(key) {
        delete this.store[key];
    }

}

global.localStorage = new LocalStorageMock();

import moment from 'moment';

export const getStartTimeForLiveCharts = () => moment().subtract(15, 'minutes').toISOString();
export const getCurrentTime = () => moment().toISOString();

const colors = [
    '#7cb5ec', '#434348', '#90ed7d', '#f7a35c', '#8085e9',
    '#f15c80', '#e4d354', '#2b908f', '#f45b5b', '#91e8e1'
];

const hashCode = (str) => {
    var hash = 0;
    if (str.length === 0) return hash;
    for (let i = 0; i < str.length; i++) {
        const char = str.charCodeAt(i);
        hash = ((hash<<5)-hash)+char;
        hash = hash & hash; // Convert to 32bit integer
    }
    return hash;
};

export default (str) => colors[Math.abs(hashCode(str)) % colors.length];
